mapper.select:
select 

from comment


    comt_id = #{comtId,jdbcType=INTEGER}


    work_id = #{workId,jdbcType=VARCHAR}


    usr_id = #{usrId,jdbcType=VARCHAR}

and del_flag = 0

mapper.update:
update comment
set del_flag = 1
where comt_id = #{comtId,jdbcType=INTEGER}

update comment
set del_flag = #{delFlag,jdbcType=VARCHAR}
where usr_id = #{usrId,jdbcType=VARCHAR}

update comment


    comt_time = #{comtTime,jdbcType=TIMESTAMP},


    comt_content = #{comtContent,jdbcType=LONGVARCHAR},


where work_id = #{workId,jdbcType=VARCHAR}
  and usr_id = #{usrId,jdbcType=VARCHAR}
