mapper.select:
select

from favoritebag
where usr_id = #{usrId,jdbcType=VARCHAR}
and favb_name = #{favbName,jdbcType=VARCHAR}
and del_flag = 0

mapper.update:
update favoritebag
set del_flag = 1
where favb_id = #{favbId,jdbcType=VARCHAR}
and usr_id = #{usrId,jdbcType=VARCHAR}

update favoritebag
set
    favb_name = #{favbName,jdbcType=VARCHAR}
where favb_id = #{favbId,jdbcType=INTEGER}
and usr_id = #{usrId,jdbcType=VARCHAR}

update favoritebag
set del_flag = #{delFlag,jdbcType=VARCHAR}
where  usr_id = #{usrId,jdbcType=VARCHAR}
